export function parseDatabaseInstructionInstruction<
  TProgram extends string,
  TAccountMetas extends readonly AccountMeta[],
>(
  instruction: Instruction<TProgram> &
    InstructionWithAccounts<TAccountMetas> &
    InstructionWithData<ReadonlyUint8Array>,
): ParsedDatabaseInstructionInstruction<TProgram, TAccountMetas> {
  if (instruction.accounts.length < 7) {
    // TODO: Coded error.
    throw new Error("Not enough accounts");
  }
  let accountIndex = 0;
  const getNextAccount = () => {
    const accountMeta = (instruction.accounts as TAccountMetas)[accountIndex]!;
    accountIndex += 1;
    return accountMeta;
  };
  const getNextOptionalAccount = () => {
    const accountMeta = getNextAccount();
    return accountMeta.address === CODE_IN_PROGRAM_ADDRESS
      ? undefined
      : accountMeta;
  };
  return {
    programAddress: instruction.programAddress,
    accounts: {
      dbRoot: getNextAccount(),
      table: getNextAccount(),
      instructionTable: getNextAccount(),
      tableRef: getNextAccount(),
      targetTableRef: getNextAccount(),
      signerAta: getNextOptionalAccount(),
      signer: getNextAccount(),
    },
    data: getDatabaseInstructionInstructionDataDecoder().decode(
      instruction.data,
    ),
  };
}
