export type ParsedRequestConnectionInstruction<
  TProgram extends string = typeof CODE_IN_PROGRAM_ADDRESS,
  TAccountMetas extends readonly AccountMeta[] = readonly AccountMeta[],
> = {
  programAddress: Address<TProgram>;
  accounts: {
    requester: TAccountMetas[0];
    dbRoot: TAccountMetas[1];
    connectionTable: TAccountMetas[2];
    instructionTable: TAccountMetas[3];
    requesterUser: TAccountMetas[4];
    receiverUser: TAccountMetas[5];
    tableRef: TAccountMetas[6];
    targetTableRef: TAccountMetas[7];
    systemProgram: TAccountMetas[8];
  };
  data: RequestConnectionInstructionData;
};

export function parseRequestConnectionInstruction<
  TProgram extends string,
  TAccountMetas extends readonly AccountMeta[],
>(
  instruction: Instruction<TProgram> &
    InstructionWithAccounts<TAccountMetas> &
    InstructionWithData<ReadonlyUint8Array>,
): ParsedRequestConnectionInstruction<TProgram, TAccountMetas> {
  if (instruction.accounts.length < 9) {
    // TODO: Coded error.
    throw new Error("Not enough accounts");
  }
  let accountIndex = 0;
  const getNextAccount = () => {
    const accountMeta = (instruction.accounts as TAccountMetas)[accountIndex]!;
    accountIndex += 1;
    return accountMeta;
  };
  return {
    programAddress: instruction.programAddress,
    accounts: {
      requester: getNextAccount(),
      dbRoot: getNextAccount(),
      connectionTable: getNextAccount(),
      instructionTable: getNextAccount(),
      requesterUser: getNextAccount(),
      receiverUser: getNextAccount(),
      tableRef: getNextAccount(),
      targetTableRef: getNextAccount(),
      systemProgram: getNextAccount(),
    },
    data: getRequestConnectionInstructionDataDecoder().decode(instruction.data),
  };
}
